const loginForm=document.querySelector("#login"),logUserIn=async a=>{a.preventDefault();const b=document.querySelector("#loginEmail").value.trim(),c=document.querySelector("#loginPassword").value.trim(),d=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({email:b,password:c}),headers:{"Content-Type":"application/json"}});d.ok?document.location.replace("/dashboard"):alert("Email or password not recognized. Please try again.")};loginForm.addEventListener("submit",logUserIn);