const searchBar=document.querySelector("#search-bar"),findTerms=()=>{const s=Array.from(document.querySelectorAll(".search-container"));for(let r=0;r<s.length;r++){s[r].strings=[];const e=s[r].querySelectorAll(".search-content");e.forEach(e=>{e=e.innerText.toLowerCase();s[r].strings.push(e)}),s[r].searchString=s[r].strings.join(" ")}return s},displayResults=(e,r)=>{const s=e.filter(e=>e.searchString.includes(r)),t=e.filter(e=>!e.searchString.includes(r));s.forEach(e=>{e.classList.add("show"),e.classList.remove("hide")}),t.forEach(e=>{e.classList.add("hide"),e.classList.remove("show")})},searchQuery=({target:e})=>{e=e.value.toLowerCase();displayResults(findTerms(),e)};searchBar.addEventListener("input",searchQuery);