const searchBar=document.querySelector("#search-bar"),searchArray=Array.from(document.querySelectorAll(".search-container"));for(let a=0;a<searchArray.length;a++){searchArray[a].strings=[];const b=searchArray[a].querySelectorAll(".search-content");b.forEach(b=>{const c=b.innerText.toLowerCase();searchArray[a].strings.push(c)}),searchArray[a].searchString=searchArray[a].strings.join(" ")}const displayResults=(a,b)=>{const c=a.filter(a=>a.searchString.includes(b)),d=a.filter(a=>!a.searchString.includes(b));c.forEach(a=>{a.classList.add("show"),a.classList.remove("hide")}),d.forEach(a=>{a.classList.add("hide"),a.classList.remove("show")})},searchQuery=({target:a})=>{displayResults(searchArray,a.value.toLowerCase())};searchBar.addEventListener("input",searchQuery);