const loginForm=document.querySelector("#login"),logUserIn=async a=>{a.preventDefault();const b=document.querySelector("#loginEmail").value.trim(),c=document.querySelector("#loginPassword").value.trim();if(b&&c){const a=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({email:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert("Email or password not recognized. Please try again.")}else b||c?b?c||alert("Enter password"):alert("Enter email"):alert("Enter email and password")};loginForm.addEventListener("submit",logUserIn);