const signupForm=document.querySelector("#signup"),registerUser=async a=>{a.preventDefault();const b=document.querySelector("#signupUsername").value.trim(),c=document.querySelector("#signupEmail").value.trim(),d=document.querySelector("#signupPassword").value.trim();if(b&&c&&d){if(8>d.length)return void alert("Password must be at least 8 characters long");const a=await fetch("/api/users/signup",{method:"POST",body:JSON.stringify({name:b,email:c,password:d}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert("Server error: Please try again")}else alert("Missing field(s)")};signupForm.addEventListener("submit",registerUser);